import SwiftUI

struct Pooling: View {
    
    private let size = 3
    @State private var output: [[Double]] = []
    @State private var currentIndexes: [[Int]] = []
    
    @State private var currentI = 0
    @State private var currentJ = 0
    
    
    var body: some View {
        HStack(spacing: 100) {
            VStack(alignment: .center, spacing: 1) {
                ForEach(Array(zip(mario.indices, mario)), id: \.0) { i, iitem in
                    HStack(alignment: .center, spacing: 1) {
                        ForEach(Array(zip(iitem.indices, iitem)), id: \.0) { j, jitem in
                            Circle()
                                .foregroundColor(Color.white)
                                .opacity(jitem)
                                .border(accColor.opacity(getOverlap(i: i, j: j) ? 1 : 0))
                                
                        }
                    }
                }
            }
            .padding()
            .border(accColor)
            VStack(alignment: .center, spacing: 1) {
                ForEach(Array(zip(output.indices, output)), id: \.0) { i, iitem in
                    HStack(alignment: .center, spacing: 1) {
                        ForEach(Array(zip(iitem.indices, iitem)), id: \.0) { j, jitem in
                            Circle()
                                .foregroundColor(Color.white)
                                .opacity(jitem)
                                .border(accColor.opacity(i == currentI && j == currentJ ? 1 : 0))
                        }
                    }
                }
            }
            .padding()
            .border(accColor)
        }
        .task() {
            await run()
        }
    }
    
    func run() async {
        // init with same size
        output = Array(repeating: Array(repeating: 0, count: mario[0].count - size + 1), count: mario.count - size + 1)
        
        var tmpArr: [[Double]] = Array(repeating: Array(repeating: 0, count: mario[0].count - size + 1), count: mario.count - size + 1)
        
        // calculate conv2d
        for i in 0...mario.count - size {
            for j in 0...mario[0].count - size {
                let tmp = mario[i..<i+size].map { $0[j..<j+size] }.flatMap { $0 }
//                tmpArr[i][j] = tmp.max()!
//                tmpArr[i][j] = tmp.min()!
//                tmpArr[i][j] = tmp.map { $0 * $0 }.reduce(0, +).squareRoot()
                let rand = arc4random_uniform(UInt32(size*size))
                tmpArr[i][j] = tmp[Int(rand)]
            }
        }
        
        try? await Task.sleep(nanoseconds: 2_000_000_000)
        
        for i in 0..<tmpArr.count {
            for j in 0..<tmpArr[i].count {
                let v = tmpArr[i][j]
                DispatchQueue.main.async {
                    output[i][j] = v
                    currentI = i
                    currentJ = j
                }
//                try? await Task.sleep(nanoseconds: 0_030_000_000)
            }
        }
    }
    
    func getOverlap(i: Int, j: Int) -> Bool {
        if i - currentI < size && i - currentI > -1 {
            if j - currentJ < size && j - currentJ > -1 {
                return true
            }
        }
        return false
    }
}

